import { React, useState } from "react";
import classes from "./Header.module.css";
import {
  MdOutlineLocalGroceryStore,
  MdArrowDropDown,
  MdSettingsInputSvideo,
} from "react-icons/md";
import { NavLink } from "react-router-dom";
import Usercontainer from "./Usercontainer";
import { AnimatePresence, motion } from "framer-motion";
const modalUser = {
  visible: {
    top: 0,
    transition: {
      when: "beforeChildren",
      staggerChildren: 0.5,
    },
  },
  hidden: {
    top: "-90vh",
  },
};
const backdrop = {
  visible: { opacity: 1 },
  hidden: { opacity: 0 },
};
const Header = () => {
  const [isOpenCU, setIsOpenCU] = useState(false);
  return (
    <div className={classes.header}>
      <div className={classes.header__inner}>
        <div className={classes.accesos}>
          <NavLink className={classes.Navaccesos} to="/main">
            <p>Accesos</p>
            <MdArrowDropDown />
          </NavLink>
        </div>
        <nav className={classes.actions}>
          <NavLink to="/main" className={classes.Navicon}>
            <MdOutlineLocalGroceryStore />
            <span className={classes.tooltiptext}>hola</span>
          </NavLink>
          <NavLink to="/main" className={classes.Navicon}>
            <MdOutlineLocalGroceryStore />
            <span className={classes.tooltiptext}>hola</span>
          </NavLink>
          <NavLink to="/main" className={classes.Navicon}>
            <MdOutlineLocalGroceryStore />
          </NavLink>
        </nav>
        <div className={classes.avatar}>
          <button
            className={classes.Navavatar}
            onClick={() => setIsOpenCU(!isOpenCU)}
          >
            <p>Hola Meyko</p>
            <img
              src="https://images.unsplash.com/photo-1621784564114-6eea05b89863?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=87&q=80"
              alt=""
            />
          </button>
        </div>
        {isOpenCU && (
          <motion.div
            className={classes.backdrop}
            variants={backdrop}
            initial="hidden"
            animate="visible"
            exit="hidden"
            onClick={() => setShowModal(false)}
          >
            <motion.div
              className={classes.modal}
              variants={modal}
              onClick={handleModalContainerClick}
            >
              <motion.div
                variants={contentmodal}
                initial="hidden"
                animate="visible"
                className={classes.content}
              >
                <div className={classes.but}>
                  <Button>
                    <div
                      className={classes.butx}
                      onClick={() => setShowModal(false)}
                    >
                      X
                    </div>
                  </Button>
                </div>
                <img src={logo} alt="logo1" />
                <h2 className={classes.subtitle}>Iniciar sesión</h2>
                <Form>
                  <p type="Email:">
                    <input placeholder="Ingrese su email.."></input>
                  </p>
                  <p type="Password:">
                    <input placeholder="Ingrese su contraseña.."></input>
                  </p>
                  <Button>Login</Button>
                </Form>
              </motion.div>
            </motion.div>
          </motion.div>
        )}
      </div>
    </div>
  );
};

export default Header;
