import React, { Fragment, useState } from "react";

import Options from "./Options";
import classes from "./OptionContainer.module.css";
import CalendarIcon from "./icons/CalendarIcon";
import { HiUsers, HiUserGroup } from "react-icons/hi";
import { MdPets, MdLocalGroceryStore } from "react-icons/md";
import { FaHandHoldingMedical } from "react-icons/fa";
import { NavLink } from "react-router-dom";

const routes = [
  {
    path: "/",
    name: "Usuarios",
    icon: <HiUsers />,
    op1: "Crear nuevo",
    op2: "Lista / Búsqueda",
    op3: "Descargar listado",
  },
  {
    path: "/",
    name: "Clientes",
    icon: <HiUserGroup />,
    op1: "Crear nuevo",
    op2: "Lista / Búsqueda",
    op3: "Descargar listado",
  },
  {
    path: "/",
    name: "Mascotas",
    icon: <MdPets />,
    op1: "Crear nuevo",
    op2: "Lista / Búsqueda",
    op3: "Descargar listado",
  },
  {
    path: "/",
    name: "Ventas",
    icon: <MdLocalGroceryStore />,
    op1: "Crear nuevo",
    op2: "Lista / Búsqueda",
    op3: "Descargar listado",
  },
  {
    path: "/",
    name: "Servicios",
    icon: <FaHandHoldingMedical />,
    op1: "Crear nuevo",
    op2: "Lista / Búsqueda",
    op3: "Descargar listado",
  },
];

const OptionContainer = () => {
  const [isHoverItems, setIsHover] = useState({
    Usuarios: false,
    Clientes: false,
    Mascotas: false,
    Ventas: false,
    Servicios: false,
    Estadisticas: false,
  });

  const mouseEnterHandler = (data) => {
    setIsHover((prev) => ({ ...prev, [data]: true }));
    console.log(data);
  };

  const mouseLeaveHandler = (data) => {
    setIsHover((prev) => ({ ...prev, [data]: false }));
    console.log("leave mouse");
  };

  return (
    <div className={classes.container}>
      {routes.map((route, index) => {
        const nombre = route.name;
        return (
          <div>
            <section
              className={classes.option}
              onMouseEnter={() => {
                mouseEnterHandler("Usuarios");
              }}
              onMouseLeave={() => {
                mouseLeaveHandler("Usuarios");
              }}
            >
              {isHoverItems.nombre ? (
                <div className={classes.opcontainer}>
                  <NavLink
                    to={route.path}
                    key={index}
                    className={classes.optionlink}
                  >
                    {route.op1}
                  </NavLink>
                  <NavLink
                    to={route.path}
                    key={index}
                    className={classes.optionlink}
                  >
                    {route.op2}
                  </NavLink>
                  <NavLink
                    to={route.path}
                    key={index}
                    className={classes.optionlink}
                  >
                    {route.op3}
                  </NavLink>
                </div>
              ) : (
                <Fragment>
                  {route.icon}
                  <p>{route.name}</p>
                </Fragment>
              )}
            </section>
          </div>
        );
      })}
    </div>
  );
};

export default OptionContainer;
