import {useState} from 'react'

export const useModal = (initialvalue= false) => {
    const [isOpen, setIsOpen]= useState(initialvalue);

    const openModal = () => {
        console.log("open modal...")
        setIsOpen(true)
    };
    const closeModal = () => {
        console.log("closing modal...")
        setIsOpen(false)
    }
    return [isOpen, openModal, closeModal];
}
