import { React, useState } from "react";
import classes from "./Header.module.css";
import Button from "../UI/Button";
import {
  MdOutlineLocalGroceryStore,
  MdArrowDropDown,
  MdSettingsInputSvideo,
} from "react-icons/md";
import { NavLink } from "react-router-dom";
import Usercontainer from "./Usercontainer";
import { AnimatePresence, motion } from "framer-motion";
const backdrop = {
  visible: { opacity: 1 },
  hidden: { opacity: 0 },
};
const showAnimation = {
  hidden: {
    width: 0,
    opacity: 0,
    transition: {
      duration: 0.5,
    },
  },
  show: {
    opacity: 1,
    width: 340,
    transition: {
      duration: 0.5,
    },
  },
};
const Header = () => {
  const [isOpenCU, setIsOpenCU] = useState(false);
  const handleModalContainerClick = (e) => e.stopPropagation();
  return (
    <div className={classes.header}>
      <div className={classes.header__inner}>
        <div className={classes.accesos}>
          <NavLink className={classes.Navaccesos} to="/main">
            <p>Accesos</p>
            <MdArrowDropDown />
          </NavLink>
        </div>
        <nav className={classes.actions}>
          <NavLink to="/main" className={classes.Navicon}>
            <MdOutlineLocalGroceryStore />
            <span className={classes.tooltiptext}>hola</span>
          </NavLink>
          <NavLink to="/main" className={classes.Navicon}>
            <MdOutlineLocalGroceryStore />
            <span className={classes.tooltiptext}>hola</span>
          </NavLink>
          <NavLink to="/main" className={classes.Navicon}>
            <MdOutlineLocalGroceryStore />
          </NavLink>
        </nav>
        <div className={classes.avatar}>
          <button
            className={classes.Navavatar}
            onClick={() => setIsOpenCU(!isOpenCU)}
          >
            <p>Hola Meyko</p>
            <img
              src="https://images.unsplash.com/photo-1621784564114-6eea05b89863?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=87&q=80"
              alt=""
            />
          </button>
        </div>
        {isOpenCU && (
          <motion.div
            className={classes.backdrop}
            variants={backdrop}
            initial="hidden"
            animate="visible"
            exit="hidden"
            onClick={() => setIsOpenCU(false)}
          >
            <AnimatePresence exitBeforeEnter>
              <motion.div
                className={classes.modal}
                variants={showAnimation}
                initial="hidden"
                animate="show"
                exit="hidden"
              >
                <motion.div>
                  <div className={classes.but}>
                    <Button>
                      <div
                        className={classes.butx}
                        onClick={() => setIsOpenCU(false)}
                      >
                        X
                      </div>
                    </Button>
                  </div>
                  <Usercontainer />
                </motion.div>
              </motion.div>
            </AnimatePresence>
          </motion.div>
        )}
      </div>
    </div>
  );
};

export default Header;
