import React, { Fragment, useState } from "react";

import Options from "./Options";
import classes from "./OptionContainer.module.css";
import UserIcon from "./icons/UserIcon";

import PawIcon from "./icons/PawIcon";
import CalendarIcon from "./icons/CalendarIcon";
import { HiUsers, HiUserGroup } from "react-icons/hi";
import { MdPets, MdLocalGroceryStore } from "react-icons/md";
import { FaHandHoldingMedical } from "react-icons/fa";
import { NavLink } from "react-router-dom";

const OptionContainer = () => {
  const [isHoverItems, setIsHover] = useState({
    user: false,
    client: false,
    pet: false,
    sale: false,
    stock: false,
    service: false,
  });

  const mouseEnterHandler = (data) => {
    setIsHover((prev) => ({ ...prev, [data]: true }));
    console.log(data);
  };

  const mouseLeaveHandler = (data) => {
    setIsHover((prev) => ({ ...prev, [data]: false }));
    console.log("leave mouse");
  };

  return (
    <div className={classes.container}>
      <section
        className={classes.option}
        onMouseEnter={() => {
          mouseEnterHandler("user");
        }}
        onMouseLeave={() => {
          mouseLeaveHandler("user");
        }}
      >
        {isHoverItems.user ? (
          <div className={classes.opcontainer}>
            <NavLink to="/main" className={classes.optionlink}>
              Crear
            </NavLink>
            <NavLink to="/main" className={classes.optionlink}>
              Crear
            </NavLink>
            <NavLink to="/main" className={classes.optionlink}>
              Crear
            </NavLink>
          </div>
        ) : (
          <Fragment>
            <HiUsers />
            <p>Usuarios</p>
          </Fragment>
        )}
      </section>
      <section
        className={classes.option}
        onMouseEnter={() => {
          mouseEnterHandler("client");
        }}
        onMouseLeave={() => {
          mouseLeaveHandler("client");
        }}
      >
        {isHoverItems.client ? (
          <div className={classes.opcontainer}>
            <NavLink to="/main" className={classes.optionlink}>
              Crear
            </NavLink>
            <NavLink to="/main" className={classes.optionlink}>
              Crear
            </NavLink>
            <NavLink to="/main" className={classes.optionlink}>
              Crear
            </NavLink>
          </div>
        ) : (
          <Fragment>
            <HiUserGroup />
            <p>Clientes</p>
          </Fragment>
        )}
      </section>
      <section
        className={classes.option}
        onMouseEnter={() => {
          mouseEnterHandler("pet");
        }}
        onMouseLeave={() => {
          mouseLeaveHandler("pet");
        }}
      >
        {isHoverItems.pet ? (
          <div className={classes.opcontainer}>
            <NavLink to="/main" className={classes.optionlink}>
              Crear
            </NavLink>
            <NavLink to="/main" className={classes.optionlink}>
              Crear
            </NavLink>
            <NavLink to="/main" className={classes.optionlink}>
              Crear
            </NavLink>
          </div>
        ) : (
          <Fragment>
            <MdPets />
            <p>Mascotas</p>
          </Fragment>
        )}
      </section>
      <section
        className={classes.option}
        onMouseEnter={() => {
          mouseEnterHandler("sale");
        }}
        onMouseLeave={() => {
          mouseLeaveHandler("sale");
        }}
      >
        {isHoverItems.sale ? (
          <Options />
        ) : (
          <Fragment>
            <MdLocalGroceryStore />
            <p>Ventas</p>
          </Fragment>
        )}
      </section>
      <section
        className={classes.option}
        onMouseEnter={() => {
          mouseEnterHandler("stock");
        }}
        onMouseLeave={() => {
          mouseLeaveHandler("stock");
        }}
      >
        {isHoverItems.stock ? (
          <Options />
        ) : (
          <Fragment>
            <CalendarIcon />
            <p>Stock</p>
          </Fragment>
        )}
      </section>
      <section
        className={classes.option}
        onMouseEnter={() => {
          mouseEnterHandler("service");
        }}
        onMouseLeave={() => {
          mouseLeaveHandler("service");
        }}
      >
        {isHoverItems.service ? (
          <Options />
        ) : (
          <Fragment>
            <FaHandHoldingMedical />
            <p>Servicios</p>
          </Fragment>
        )}
      </section>
    </div>
  );
};

export default OptionContainer;
