import React, { Fragment, useState } from "react";

import Options from "./Options";
import classes from "./OptionContainer.module.css";
import CalendarIcon from "./icons/CalendarIcon";
import { HiUsers, HiUserGroup } from "react-icons/hi";
import { MdPets, MdLocalGroceryStore } from "react-icons/md";
import { FaHandHoldingMedical } from "react-icons/fa";
import { NavLink } from "react-router-dom";

const routes = [
  {
    path: "/",
    name: "Usuarios",
    icon: <HiUsers />,
    op1: "Crear nuevo",
    op2: "Lista / Búsqueda",
    op3: "Descargar listado",
  },
  {
    path: "/",
    name: "Clientes",
    icon: <HiUserGroup />,
    op1: "Crear nuevo",
    op2: "Lista / Búsqueda",
    op3: "Descargar listado",
  },
  {
    path: "/",
    name: "Mascotas",
    icon: <MdPets />,
    op1: "Crear nuevo",
    op2: "Lista / Búsqueda",
    op3: "Descargar listado",
  },
  {
    path: "/",
    name: "Ventas",
    icon: <MdLocalGroceryStore />,
    op1: "Crear nuevo",
    op2: "Lista / Búsqueda",
    op3: "Descargar listado",
  },
  {
    path: "/",
    name: "Servicios",
    icon: <FaHandHoldingMedical />,
    op1: "Crear nuevo",
    op2: "Lista / Búsqueda",
    op3: "Descargar listado",
  },
];

const OptionContainer = () => {
  const [isHoverItems, setIsHover] = useState({
    user: false,
    client: false,
    pet: false,
    sale: false,
    stock: false,
    service: false,
  });

  const mouseEnterHandler = (data) => {
    setIsHover((prev) => ({ ...prev, [data]: true }));
    console.log(data);
  };

  const mouseLeaveHandler = (data) => {
    setIsHover((prev) => ({ ...prev, [data]: false }));
    console.log("leave mouse");
  };

  return (
    <div className={classes.container}>
      {routes.map((route, index) => {
        return (
          <section
            className={classes.option}
            onMouseEnter={() => {
              mouseEnterHandler("client");
            }}
            onMouseLeave={() => {
              mouseLeaveHandler("client");
            }}
          >
            {isHoverItems.client ? (
              <div className={classes.opcontainer}>
                <NavLink to="/main" className={classes.optionlink}>
                  Crear
                </NavLink>
                <NavLink to="/main" className={classes.optionlink}>
                  Crear
                </NavLink>
                <NavLink to="/main" className={classes.optionlink}>
                  Crear
                </NavLink>
              </div>
            ) : (
              <Fragment>
                <HiUserGroup />
                <p>Clientes</p>
              </Fragment>
            )}
          </section>
        );
      })}
    </div>
  );
};

export default OptionContainer;
