import React from "react";
import Button from "./Button";
import classes from "./Modal.module.css";

const Modal = (props) => {
  const handleModalContainerClick = (e) => e.stopPropagation();
  console.log(props.isOpen);
  return (
    <React.Fragment>
      {props.isOpen && (
        <article className={classes.Modal} onClick={props.closeModal}>
          <div className={classes.ModalIsOpen}>
            <div
              className={classes.ModalContainer}
              onClick={handleModalContainerClick}
            >
              <button class={classes.modalClose} onClick={props.closeModal}>
                x
              </button>
              {props.children}
            </div>
          </div>
        </article>
      )}
    </React.Fragment>
  );
};

export default Modal;
