import { React, Fragment, useState, useEffect } from "react";
import classes from "./SideBarItem.module.css";
import { HiUsers, HiUserGroup } from "react-icons/hi";

import { MdArrowDropDown, MdPets } from "react-icons/md";
import SideBarOptions from "./SideBarOptions";
import { NavLink } from "react-router-dom";

const SideBarItem = (props) => {
  const [expanded, SetExpanded] = useState({
    user: false,
    client: false,
    pet: false,
  });

  const auxobject = { user: false, client: false, pet: false };

  function handleExpanded(data) {
    auxobject[data] = !expanded[data];
    for (var valor in expanded) {
      if (expanded[valor] != expanded[data]) auxobject[valor] = false;
    }
    console.log(expanded);
    SetExpanded(auxobject);
  }
  const { isOpen, isOpenHamburger } = props;
  useEffect(() => {
    if (!isOpenHamburger) {
      for (var valor in expanded) {
        auxobject[valor] = false;
      }
      SetExpanded(auxobject);
    }
  }, [isOpen, isOpenHamburger]);

  return (
    <div className={classes.SideBarItem}>
      <div
        onClick={handleExpanded.bind(null, "user")}
        className={
          props.isOpen || props.isOpenHamburger
            ? classes.Itemexpanded
            : classes.Itemcollapsed
        }
      >
        <Fragment>
          <HiUsers />
          <p>Usuarios</p>
          <div className={classes.fle}>
            <MdArrowDropDown />
          </div>
        </Fragment>
      </div>
      {expanded.user ? (
        <div className={classes.Sidebarcontentopcion}>
          <NavLink to="/main" className={classes.Sidebaropcion}>
            - Lista / Busqueda
          </NavLink>
          <NavLink to="/main" className={classes.Sidebaropcion}>
            - Lista / Busqueda
          </NavLink>
          <NavLink to="/main" className={classes.Sidebaropcion}>
            - Lista / Busqueda
          </NavLink>
        </div>
      ) : (
        <div></div>
      )}

      <div
        onClick={handleExpanded.bind(null, "client")}
        className={
          props.isOpen || props.isOpenHamburger
            ? classes.Itemexpanded
            : classes.Itemcollapsed
        }
      >
        <Fragment>
          <HiUserGroup />
          <p>Clientes</p>
          <div className={classes.fle}>
            <MdArrowDropDown />
          </div>
        </Fragment>
      </div>
      {expanded.client ? (
        <div className={classes.Sidebarcontentopcion}>
          <NavLink to="/main" className={classes.Sidebaropcion}>
            - Lista / Busqueda
          </NavLink>
          <NavLink to="/main" className={classes.Sidebaropcion}>
            - Lista / Busqueda
          </NavLink>
          <NavLink to="/main" className={classes.Sidebaropcion}>
            - Lista / Busqueda
          </NavLink>
        </div>
      ) : (
        <div></div>
      )}
      <div
        onClick={handleExpanded.bind(null, "pet")}
        className={
          props.isOpen || props.isOpenHamburger
            ? classes.Itemexpanded
            : classes.Itemcollapsed
        }
      >
        <Fragment>
          <MdPets />
          <p>Pacientes</p>
          <div className={classes.fle}>
            <MdArrowDropDown />
          </div>
        </Fragment>
      </div>
      {expanded.pet ? (
        <div className={classes.Sidebarcontentopcion}>
          <NavLink to="/main" className={classes.Sidebaropcion}>
            - Lista / Busqueda
          </NavLink>
          <NavLink to="/main" className={classes.Sidebaropcion}>
            - Lista / Busqueda
          </NavLink>
          <NavLink to="/main" className={classes.Sidebaropcion}>
            - Lista / Busqueda
          </NavLink>
        </div>
      ) : (
        <div></div>
      )}
    </div>
  );
};

export default SideBarItem;

